mapper.select:
select count(*) from message

    poster_id = #{stuId}

        and chat_id = #{chatId}

select * from message where
poster_id = #{stuId} and chat_id = #{chatId}
order by create_time desc
limit #{offset}, #{limit};

select * from message
where chat_id = #{chatId}
order by c by create_time desc
limit 0, 1;
