mapper.update:
update user
set username = #{username}
where stu_id = #{stuId}

update user
set password = #{password}
where stu_id = #{stuId}

update user
set header_url = #{headerUrl}
where stu_id = #{stuId}

mapper.select:
select stu_id, username, password, create_at as createTime, header_url, gender, salt
from user
where stu_id = #{stuId}
